!function () {
    "use strict";

    class e {
        static initValidationSignUp() {
            Codebase.helpers("jq-validation"), jQuery(".js-validation-signup").validate({
                rules: {
                    "name": {required: !0},
                    "email": {required: !0, emailWithDot: !0},
                    "password": {required: !0, minlength: 8},
                    "password-confirm": {required: !0, equalTo: "#password"},
                    "signup-terms": {required: !0}
                },
                messages: {
                    "name": {
                        required: "Please enter a name",
                    },
                    "email": "Please enter a valid email address",
                    "password": {
                        required: "Please provide a password",
                        minlength: "Your password must be at least 8 characters long"
                    },
                    "password-confirm": {
                        required: "Please provide a password",
                        minlength: "Your password must be at least 8 characters long",
                        equalTo: "Please enter the same password as above"
                    },
                    "signup-terms": "You must agree to the service terms!"
                }
            })
        }

        static init() {
            this.initValidationSignUp()
        }
    }

    Codebase.onLoad((() => e.init()))
}();